export declare const enum ErrorCode {
    Unknown = 4000,
    PurgeUserNotFound = 4001,
    PurgeNoPermissions = 4002,
    MessageNotFound = 4003,
    BadRequest = 4004,
    RateLimited = 4005,
    AuthServerError = 4006
}
export declare const enum StringError {
    UserNotFound = "UNOTFOUND",
    AccessDenied = "UACCESS"
}
/**
 * Format of the response packet received in response
 * to a method call through the chat socket
 */
export interface IPacket {
    id: number;
    type: string;
    event: any;
    data: any;
    error: null | StringError | IErrorPacket;
}
export interface IErrorPacket {
    code: ErrorCode;
    message: string;
    stacktrace: string;
}
